def is_stockmarket_related(tweet):
    # Convert tweet to lowercase and check for stock-related keywords
    words = set(tweet.lower().split())
    # Find matching keywords
    matching_keywords = stock_keywords.intersection(words)
    matching_codes = stock_codes.intersection(words)

    # Debugging: Print the matches
    if matching_keywords or matching_codes:
        print(f"Tweet: {tweet}")
        print(f"Matching Keywords: {matching_keywords}")
        print(f"Matching Codes: {matching_codes}\n")

        # Return True if any matches are found
    return bool(matching_keywords or matching_codes)

